package com.hk.app;

public class Cast2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'F';
		System.out.println(c);
		int result = c; // 2byte - 4byte
		System.out.println(result);
		// 71?
		int i = 71;
		char c2 = (char)i;
		System.out.println(c2);
	}

}
