package chap03;

public class ex06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 5;
		
		System.out.println( (x>7) && (y<=5) ); // x>7 이고 y<=5를 and 비교
		System.out.println( (x%3 == 2) || (y%2 != 1) );
		// x를 3으로 나눈 값이 2이거나 y를 2로 나눈 나머지가 1이 아닌 경우를 or 비교

	}

}
